package main

import (
	"fmt"
	"strconv"
	"strings"
)

func part1(s string) {
	fmt.Println("Answer #1=" + strconv.Itoa(strings.Count(s, "(")-strings.Count(s, ")")))
}

func part2(s string) int {
	floor := 0
	for i, r := range s {
		if r == '(' {
			floor += 1
		} else if r == ')' {
			floor -= 1
		} else {
			fmt.Printf("Unrecognized floor direction \"%c\"\n", r)
		}
		if floor == -1 {
			return i + 1
		}
	}
	return -1
}

